import { AfterViewInit, Component } from '@angular/core';

declare const M: any;

@Component({
  selector: 'app-carousel-items',
  templateUrl: './carousel-items.component.html',
  styleUrls: ['./carousel-items.component.css']
})
export class CarouselItemsComponent implements AfterViewInit {
  ngAfterViewInit(): void {
    M.Carousel.init(document.querySelectorAll('.carousel'), {
      fullWidth: true,
      indicators: true,
      interval: 3000
    });
  }

  images = [
    'assets/imagens/bolo_baria_inteiro.JPG',
    'assets/imagens/bolo_branca_neve.JPG',
    'assets/imagens/bolo_moinho.JPG'
  ];
}
